export const api = {
  activeSessionUser: {
    url: "auth/users/session/active",
    method: "post",
  },
};

export const apiService = {
  getBaseUrl: () => {
    return `${window.location.protocol}//api.farmer.accounts.easyfarm.co.in/`;
  },
  postData: async (apiEntity, data) => {
    const url = `${window.location.protocol}//api.farmer.accounts.easyfarm.co.in/${apiEntity.url}`;
    console.log("--==>> PostData URL ", url);

    const request = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "x-access-token": sessionStorage.getItem("token"),
      },
      redirect: "follow",
      referrerPolicy: "no-referrer",
    };
    if (data) {
      request.body = JSON.stringify(data);
    }

    return await fetch(url, request).json();
  },
};
