import React from "react";
import styled from "styled-components";

const AccordionMenu = styled.div`
    font-family: 'Open Sans', sans-serif;
    box-sizing: border-box;
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    outline: 0;
    min-width: 260px;
	float: left;
	position: relative;
    box-shadow: 0 20px 50px #333;
    width: 100%;
	height: 50px;
	padding-left: 22px;
	float: left;
	line-height: 50px;
	font-weight: 600;
	color: #f0f0f0;
    background: #414956
    ul {
        margin: 0;
        padding: 0;
        list-style: none
    }

    ul li {
        width: 100%;
        display: block;
        float: left;
        position: relative
    }

    ul li a {
        width: 100%;
        padding: 14px 22px;
        float: left;
        text-decoration: none;
        color: #f0f0f0;
        font-size: 13px;
        background: #414956;
        white-space: nowrap;
        position: relative;
        overflow: hidden;
        -o-transition: color .2s linear, background .2s linear;
        -moz-transition: color .2s linear, background .2s linear;
        -webkit-transition: color .2s linear, background .2s linear;
        transition: color .2s linear, background .2s linear
    }

    ul>li.active>a, ul>li:hover>a {
	    color: #fff;
	    background: #3b424d
    }

    ul>li>a {
        border-bottom: solid 1px #3b424d
    }

    ul li a i {
        width: 34px;
        float: left;
        line-height: 18px;
        font-size: 16px;
        text-align: left
    }

    .submenu-indicator {
        float: right;
        right: 22px;
        position: absolute;
        line-height: 19px;
        font-size: 20px;
        -o-transition: transform .3s linear;
        -moz-transition: transform .3s linear;
        -webkit-transition: transform .3s linear;
        -ms-transition: transform .3s linear
    }

    ul ul.submenu .submenu-indicator {
        line-height: 16px
    }
    .submenu-indicator-minus>.submenu-indicator {
        -ms-transform: rotate(45deg);
        -moz-transform: rotate(45deg);
        -webkit-transform: rotate(45deg);
        transform: rotate(45deg)
    }
    
    ul ul.submenu, ul ul.submenu li ul.submenu {
        width: 100%;
        display: none;
        position: static
    }
`;

export default function AppNavigationMenu() {
  return (
    <React.Fragment>
      <AccordionMenu id="jquery-accordion-menu" class="jquery-accordion-menu">
        <div class="jquery-accordion-menu-header">Header </div>
        <ul>
          <li class="active">
            <a href="#">
              <i class="fa fa-home"></i>Home{" "}
            </a>
          </li>
          <li>
            <a href="#">
              <i class="fa fa-glass"></i>Events{" "}
            </a>
          </li>
          <li>
            <a href="#">
              <i class="fa fa-file-image-o"></i>Gallery{" "}
            </a>
            <span class="jquery-accordion-menu-label">12 </span>
          </li>
          <li>
            <a href="#">
              <i class="fa fa-cog"></i>Services{" "}
            </a>
            <ul class="submenu">
              <li>
                <a href="#">Web Design </a>
              </li>
              <li>
                <a href="#">Hosting </a>
              </li>
              <li>
                <a href="#">Design </a>
                <ul class="submenu">
                  <li>
                    <a href="#">Graphics </a>
                  </li>
                  <li>
                    <a href="#">Vectors </a>
                  </li>
                  <li>
                    <a href="#">Photoshop </a>
                  </li>
                  <li>
                    <a href="#">Fonts </a>
                  </li>
                </ul>
              </li>
              <li>
                <a href="#">Consulting </a>
              </li>
            </ul>
          </li>
          <li>
            <a href="#">
              <i class="fa fa-newspaper-o"></i>News{" "}
            </a>
          </li>
          <li>
            <a href="#">
              <i class="fa fa-suitcase"></i>Portfolio{" "}
            </a>
            <ul class="submenu">
              <li>
                <a href="#">Web Design </a>
              </li>
              <li>
                <a href="#">Graphics </a>
                <span class="jquery-accordion-menu-label">10 </span>
              </li>
              <li>
                <a href="#">Photoshop </a>
              </li>
              <li>
                <a href="#">Programming </a>
              </li>
            </ul>
          </li>
          <li>
            <a href="#">
              <i class="fa fa-user"></i>About{" "}
            </a>
          </li>
          <li>
            <a href="#">
              <i class="fa fa-envelope"></i>Contact{" "}
            </a>
          </li>
        </ul>
        <div class="jquery-accordion-menu-footer">Footer </div>
      </AccordionMenu>
    </React.Fragment>
  );
}
