import React from "react";
import styled from "styled-components";
import {
  HomeWork,
  MiscellaneousServices,
  ContactPage,
} from "@styled-icons/material";
import { WineGlassAlt, Images, FileImport } from "@styled-icons/fa-solid";

export default function AppNavigationMenu() {
  const accordionMenuConfig = [
    {
      id: "homeLink",
      displayName: "Home2",
      iconComponent: HomeWork,
      className: "active",
      subMenu: [
        {
          id: "accounts",
          displayName: "Accounts",
          iconComponent: WineGlassAlt,
        },
        {
          id: "assets",
          displayName: "Assets",
          iconComponent: MiscellaneousServices,
          subMenu: [
            {
              id: "accounts",
              displayName: "Accounts",
              iconComponent: WineGlassAlt,
            },
            {
              id: "assets",
              displayName: "Assets",
              iconComponent: MiscellaneousServices,
            },
            {
              id: "reports",
              displayName: "Reports",
              iconComponent: FileImport,
            },
          ],
        },
        { id: "reports", displayName: "Reports", iconComponent: FileImport },
      ],
    },
  ];

  const onToggleSubmenu = (event, ulId) => {
    event.preventDefault();
    event.stopPropagation();
    const currentState = $(`#${ulId}`).css("display");
    if (currentState === "none") {
        $(`#${ulId}`).css("display", "block");
    } else {
        $(`#${ulId}`).css("display", "none");
    }
    
  };

  const getLinkTag = (item) => {
    const randomUniqueId = `submenuId${new Date().getTime()}_${item.id}`;

    return (
      <li key={item.id}>
        <a href="#">
          {React.createElement(item.iconComponent, {
            size: "20",
            color: "#fff",
          })}
          {item.displayName}
          {item.subMenu && (
            <span
              onClick={(event) => onToggleSubmenu(event, randomUniqueId)}
              class="submenu-indicator"
            >
              +
            </span>
          )}
        </a>
        {item.subMenu && (
          <ul class="submenu" key={randomUniqueId} id={randomUniqueId}>
            {item.subMenu.map((entity) => {
              return getLinkTag(entity);
            })}
          </ul>
        )}
      </li>
    );
  };

  return (
    <React.Fragment>
      <div id="jquery-accordion-menu" className="jquery-accordion-menu">
        <div className="jquery-accordion-menu-header">Header </div>
        <ul>
          {accordionMenuConfig.map((item) => {
            return getLinkTag(item);
          })}
          <li>
            <a href="#">
              <WineGlassAlt size="20" />
              Events{" "}
            </a>
          </li>
          <li>
            <a href="#">
              <Images size="20" />
              Gallery{" "}
            </a>
            <span className="jquery-accordion-menu-label">12 </span>
          </li>
          <li>
            <a href="#">
              <MiscellaneousServices size="20" />
              Services{" "}
            </a>
            <ul className="submenu">
              <li>
                <a href="#">Web Design </a>
              </li>
              <li>
                <a href="#">Hosting </a>
              </li>
              <li>
                <a href="#">Design </a>
                <ul className="submenu">
                  <li>
                    <a href="#">Graphics </a>
                  </li>
                  <li>
                    <a href="#">Vectors </a>
                  </li>
                  <li>
                    <a href="#">Photoshop </a>
                  </li>
                  <li>
                    <a href="#">Fonts </a>
                  </li>
                </ul>
              </li>
              <li>
                <a href="#">Consulting </a>
              </li>
            </ul>
          </li>
          <li>
            <a href="#">
              <FileImport size="20" />
              News{" "}
            </a>
          </li>
          <li>
            <a href="#">
              <i className="fa fa-suitcase"></i>Portfolio{" "}
            </a>
            <ul className="submenu">
              <li>
                <a href="#">Web Design </a>
              </li>
              <li>
                <a href="#">Graphics </a>
                <span className="jquery-accordion-menu-label">10 </span>
              </li>
              <li>
                <a href="#">Photoshop </a>
              </li>
              <li>
                <a href="#">Programming </a>
              </li>
            </ul>
          </li>
          <li>
            <a href="#">
              <i className="fa fa-user"></i>About{" "}
            </a>
          </li>
          <li>
            <a href="#">
              <ContactPage size="20" />
              Contact{" "}
            </a>
          </li>
        </ul>
        <div className="jquery-accordion-menu-footer">Footer </div>
      </div>
    </React.Fragment>
  );
}
