import React from "react";
import styled from "styled-components";
import { useSelector, useDispatch } from "react-redux";
import {
  Dashboard,
  VideogameAsset,
  CalendarToday,
  ContactPage,
  HelpCenter,
} from "@styled-icons/material";
import { CalendarAlt, CalendarCheck, Wallet } from "@styled-icons/fa-solid";
import { ScikitLearn, Youtube, Blogger } from "@styled-icons/simple-icons";
import { getSideMenuMetaData } from "./../../../store/actions";

export default function AppNavigationMenu() {
  const dispatch = useDispatch();
  const { metaData } = useSelector((state) => {
    return {
      metaData: state.MetaData,
    };
  });
  console.log("--== metaData: state.MetaData ", metaData);
  React.useEffect(() => {
      debugger;
    dispatch(getSideMenuMetaData());
  }, []);
  //   const { sideMenuMetaInfo } = metaData;
  const sideMenuMetaInfo = [
    {
      id: "dashboardLink",
      displayName: "Dashboard",
      iconComponent: Dashboard,
      className: "active",
      type: "link",
    },
    {
      id: "walletLink",
      displayName: "My Wallet",
      iconComponent: Wallet,
      type: "link",
    },
    {
      id: "helpCenter",
      displayName: "Help Center",
      iconComponent: HelpCenter,
      type: "link",
    },
    {
      id: "calendarLink",
      displayName: "Calendar",
      iconComponent: CalendarAlt,
      type: "link",
      subMenu: [
        {
          id: "calendarToday",
          displayName: "My Day",
          iconComponent: CalendarToday,
          type: "link",
        },
        {
          id: "calendarCrop",
          displayName: "Crop Calendar",
          iconComponent: CalendarCheck,
          type: "link",
        },
      ],
    },
    {
      id: "contactsLink",
      displayName: "Phone Book",
      iconComponent: ContactPage,
      type: "link",
    },
    {
      id: "assetsLink",
      displayName: "Assets",
      iconComponent: VideogameAsset,
      type: "link",
      subMenu: [
        {
          type: "info",
          displayName: "No Assets Registered",
        },
      ],
    },
    {
      id: "learningLink",
      displayName: "Learnings",
      iconComponent: ScikitLearn,
      type: "link",
      subMenu: [
        {
          id: "videoLearningLink",
          displayName: "Videos",
          iconComponent: Youtube,
          type: "link",
        },
        {
          id: "blogLink",
          displayName: "Blog",
          iconComponent: Blogger,
          type: "link",
        },
      ],
    },
  ];

  const onLiClick = (event, item) => {
    event.preventDefault();
    event.stopPropagation();
    sideMenuMetaInfo.forEach((entity) => {
      if (entity.id === item.id) {
        entity.className = null;
      }
    });
    item.className = "active";
  };

  const onToggleSubmenu = (event, item, ulId) => {
    event.preventDefault();
    event.stopPropagation();
    console.log("--== onToggleSubmenu ", event);
    const currentState = $(`#${ulId}`).css("display");
    if (currentState === "none") {
      $(event.target).text("-");
      $(`#${ulId}`).css("display", "block");
    } else {
      $(event.target).text("+");
      $(`#${ulId}`).css("display", "none");
    }
  };

  const getLinkTag = (item) => {
    const randomUniqueId = `submenuId${new Date().getTime()}_${item.id}`;

    return (
      <li
        className={item.className}
        key={item.id}
        onClick={(event) => onLiClick(event, item)}
      >
        <React.Fragment>
          {item.type === "link" && (
            <React.Fragment>
              <a href="#">
                {item.iconComponent &&
                  React.createElement(item.iconComponent, {
                    size: "20",
                    color: "#fff",
                  })}
                <span className="ml-1">{item.displayName}</span>
                {item.subMenu && (
                  <span
                    onClick={(event) =>
                      onToggleSubmenu(event, item, randomUniqueId)
                    }
                    className="submenu-indicator"
                  >
                    +
                  </span>
                )}
              </a>
            </React.Fragment>
          )}
          {item.type === "info" && (
            <a href="#" onClick={(event) => event.preventDefault()}>
              {item.displayName}
            </a>
          )}
          {item.subMenu && (
            <ul className="submenu" key={randomUniqueId} id={randomUniqueId}>
              {item.subMenu.map((entity) => {
                return getLinkTag(entity);
              })}
            </ul>
          )}
        </React.Fragment>
      </li>
    );
  };

  return (
    <React.Fragment>
      {sideMenuMetaInfo && sideMenuMetaInfo.length > 0 && (
        <div id="jquery-accordion-menu" className="jquery-accordion-menu">
          <ul>
            {sideMenuMetaInfo.map((item) => {
              return getLinkTag(item);
            })}
          </ul>
        </div>
      )}
    </React.Fragment>
  );
}
