import React from "react";
import ReactDOM from "react-dom";
import styled from "styled-components";
import {
  HomeWork,
  MiscellaneousServices,
  ContactPage,
} from "@styled-icons/material";
import { WineGlassAlt, Images, FileImport } from "@styled-icons/fa-solid";

export default function AppNavigationMenu() {
  React.useEffect(() => {
    const accordionMenu = $("#jquery-accordion-menu").find(".submenu");
    if (accordionMenu.length > 3) {
      accordionMenu
        .siblings("a")
        .append("<span class='submenu-indicator'>+</span>");
      accordionMenu.siblings("a").on("click", (event) => {
        event.preventDefault();
        event.stopPropagation();
        console.log("--==>> event target ", event, event.target);
      });
    }
  }, []);

  const accordionMenuConfig = [
    {
      displayName: "Home",
      iconComponent: HomeWork,
      className: "active",
      subMenu: [{ displayName: "Accounts" }, { displayName: "Assets" }],
    },
  ];

  return (
    <React.Fragment>
      <div id="jquery-accordion-menu" class="jquery-accordion-menu">
        <div class="jquery-accordion-menu-header">Header </div>
        <ul>
          {accordionMenuConfig.map((item, index) => {
            return (
              <li className={item.className}>
                <a href="#">
                  {React.createElement(item.iconComponent, {
                    size: "20",
                    color: "#fff",
                  })}{" "}
                  {item.displayName}
                  {item.subMenu && <span class="submenu-indicator">+</span>}
                </a>

                {item.subMenu && (
                  <ul class="submenu" style={{display:"block"}}>
                    {item.subMenu.map((entity) => {
                      return (
                        <li>
                          <a href="#">{entity.displayName} </a>
                        </li>
                      );
                    })}
                  </ul>
                )}
              </li>
            );
          })}
          <li>
            <a href="#">
              <WineGlassAlt size="20" />
              Events{" "}
            </a>
          </li>
          <li>
            <a href="#">
              <Images size="20" />
              Gallery{" "}
            </a>
            <span class="jquery-accordion-menu-label">12 </span>
          </li>
          <li>
            <a href="#">
              <MiscellaneousServices size="20" />
              Services{" "}
            </a>
            <ul class="submenu">
              <li>
                <a href="#">Web Design </a>
              </li>
              <li>
                <a href="#">Hosting </a>
              </li>
              <li>
                <a href="#">Design </a>
                <ul class="submenu">
                  <li>
                    <a href="#">Graphics </a>
                  </li>
                  <li>
                    <a href="#">Vectors </a>
                  </li>
                  <li>
                    <a href="#">Photoshop </a>
                  </li>
                  <li>
                    <a href="#">Fonts </a>
                  </li>
                </ul>
              </li>
              <li>
                <a href="#">Consulting </a>
              </li>
            </ul>
          </li>
          <li>
            <a href="#">
              <FileImport size="20" />
              News{" "}
            </a>
          </li>
          <li>
            <a href="#">
              <i class="fa fa-suitcase"></i>Portfolio{" "}
            </a>
            <ul class="submenu">
              <li>
                <a href="#">Web Design </a>
              </li>
              <li>
                <a href="#">Graphics </a>
                <span class="jquery-accordion-menu-label">10 </span>
              </li>
              <li>
                <a href="#">Photoshop </a>
              </li>
              <li>
                <a href="#">Programming </a>
              </li>
            </ul>
          </li>
          <li>
            <a href="#">
              <i class="fa fa-user"></i>About{" "}
            </a>
          </li>
          <li>
            <a href="#">
              <ContactPage size="20" />
              Contact{" "}
            </a>
          </li>
        </ul>
        <div class="jquery-accordion-menu-footer">Footer </div>
      </div>
    </React.Fragment>
  );
}
