import React from "react";
import styled from "styled-components";
import {
  Dashboard,
  VideogameAsset,
  CalendarToday,
  ContactPage,
} from "@styled-icons/material";
import { CalendarAlt, CalendarCheck } from "@styled-icons/fa-solid";
import { Youtube } from "@styled-icons/simple-icons";

export default function AppNavigationMenu() {
  const accordionMenuConfig = [
    {
      id: "dashboardLink",
      displayName: "Dashboard",
      iconComponent: Dashboard,
      className: "active",
      type: "link",
    },
    {
      id: "assetsLink",
      displayName: "Assets",
      iconComponent: VideogameAsset,
      type: "link",
      subMenu: [
        {
          type: "info",
          displayName: "No Assets Registered",
        },
      ],
    },
    {
      id: "calendarLink",
      displayName: "Calendar",
      iconComponent: CalendarAlt,
      type: "link",
      subMenu: [
        {
          id: "calendarToday",
          displayName: "Today",
          iconComponent: CalendarToday,
          type: "link",
        },
        {
          id: "calendarCrop",
          displayName: "Crop Calendar",
          iconComponent: CalendarCheck,
          type: "link",
        },
      ],
    },
    {
      id: "contactsLink",
      displayName: "Phone Book",
      iconComponent: ContactPage,
      type: "link",
    },
    {
      id: "youtubeLink",
      displayName: "Youtube",
      iconComponent: Youtube,
      type: "link",
    },
  ];

  const onToggleSubmenu = (event, item, ulId) => {
    event.preventDefault();
    event.stopPropagation();
    console.log("--== onToggleSubmenu ", event);
    const currentState = $(`#${ulId}`).css("display");
    if (currentState === "none") {
      $(event.target).text("-");
      $(`#${ulId}`).css("display", "block");
    } else {
      $(event.target).text("+");
      $(`#${ulId}`).css("display", "none");
    }
  };

  const getLinkTag = (item) => {
    const randomUniqueId = `submenuId${new Date().getTime()}_${item.id}`;

    return (
      <li key={item.id}>
        <React.Fragment>
          {item.type === "link" && (
            <React.Fragment>
              <a href="#">
                {item.iconComponent &&
                  React.createElement(item.iconComponent, {
                    size: "20",
                    color: "#fff",
                  })}
                <span className="ml-1">{item.displayName}</span>
                {item.subMenu && (
                  <span
                    onClick={(event) =>
                      onToggleSubmenu(event, item, randomUniqueId)
                    }
                    class="submenu-indicator"
                  >
                    +
                  </span>
                )}
              </a>
            </React.Fragment>
          )}
          {item.type === "info" && item.displayName}
          {item.subMenu && (
            <ul class="submenu" key={randomUniqueId} id={randomUniqueId}>
              {item.subMenu.map((entity) => {
                return getLinkTag(entity);
              })}
            </ul>
          )}
        </React.Fragment>
      </li>
    );
  };

  return (
    <React.Fragment>
      <div id="jquery-accordion-menu" className="jquery-accordion-menu">
        <ul>
          {accordionMenuConfig.map((item) => {
            return getLinkTag(item);
          })}
        </ul>
      </div>
    </React.Fragment>
  );
}
