import React from "react";
import styled from "styled-components";
import {
  Dashboard,
  VideogameAsset,
  CalendarToday,
  ContactPage,
} from "@styled-icons/material";
import { CalendarAlt, CalendarCheck } from "@styled-icons/fa-solid";
import { ScikitLearn, Youtube, Blogger } from "@styled-icons/simple-icons";

export default function AppNavigationMenu() {
  const accordionMenuConfig = [
    {
      id: "dashboardLink",
      displayName: "Dashboard",
      iconComponent: Dashboard,
      className: "active",
      type: "link",
    },
    {
      id: "assetsLink",
      displayName: "Assets",
      iconComponent: VideogameAsset,
      type: "link",
      subMenu: [
        {
          type: "info",
          displayName: "No Assets Registered",
        },
      ],
    },
    {
      id: "calendarLink",
      displayName: "Calendar",
      iconComponent: CalendarAlt,
      type: "link",
      subMenu: [
        {
          id: "calendarToday",
          displayName: "My Day",
          iconComponent: CalendarToday,
          type: "link",
        },
        {
          id: "calendarCrop",
          displayName: "Crop Calendar",
          iconComponent: CalendarCheck,
          type: "link",
        },
      ],
    },
    {
      id: "contactsLink",
      displayName: "Phone Book",
      iconComponent: ContactPage,
      type: "link",
    },
    {
      id: "learningLink",
      displayName: "Learnings",
      iconComponent: ScikitLearn,
      type: "link",
      subMenu: [
          {
            id: "videoLearningLink",
            displayName: "Videos",
            iconComponent: Youtube,
            type: "link",
          },
          {
            id: "blogLink",
            displayName: "Blog",
            iconComponent: Blogger,
            type: "link",
          }
      ]
    },
  ];

  const onLiClick = (event, item) => {
    event.preventDefault();
    event.stopPropagation();
    accordionMenuConfig.forEach((entity) => {
        if (entity.id === item.id) {
            entity.className = null;
        }
    });
    item.className = "active";
  }

  const onToggleSubmenu = (event, item, ulId) => {
    event.preventDefault();
    event.stopPropagation();
    console.log("--== onToggleSubmenu ", event);
    const currentState = $(`#${ulId}`).css("display");
    if (currentState === "none") {
      $(event.target).text("-");
      $(`#${ulId}`).css("display", "block");
    } else {
      $(event.target).text("+");
      $(`#${ulId}`).css("display", "none");
    }
  };

  const getLinkTag = (item) => {
    const randomUniqueId = `submenuId${new Date().getTime()}_${item.id}`;

    return (
      <li className={item.className} key={item.id} onClick={(event) => onLiClick(event, item)}>
        <React.Fragment>
          {item.type === "link" && (
            <React.Fragment>
              <a href="#">
                {item.iconComponent &&
                  React.createElement(item.iconComponent, {
                    size: "20",
                    color: "#fff",
                  })}
                <span className="ml-1">{item.displayName}</span>
                {item.subMenu && (
                  <span
                    onClick={(event) =>
                      onToggleSubmenu(event, item, randomUniqueId)
                    }
                    class="submenu-indicator"
                  >
                    +
                  </span>
                )}
              </a>
            </React.Fragment>
          )}
          {item.type === "info" && (
              <a href="#" onClick={(event) => event.preventDefault()}>{item.displayName}</a>
          )}
          {item.subMenu && (
            <ul class="submenu" key={randomUniqueId} id={randomUniqueId}>
              {item.subMenu.map((entity) => {
                return getLinkTag(entity);
              })}
            </ul>
          )}
        </React.Fragment>
      </li>
    );
  };

  return (
    <React.Fragment>
      <div id="jquery-accordion-menu" className="jquery-accordion-menu">
        <ul>
          {accordionMenuConfig.map((item) => {
            return getLinkTag(item);
          })}
        </ul>
      </div>
    </React.Fragment>
  );
}
